for i in $(seq 1 5); do filename=$(hostname | sed 's/\./_/g')_$(openssl rand -hex 6).csv; for i in $(seq 1 100); do echo $(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')),$(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')),$(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')),$(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')),$(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')),$(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')),$(echo $(od -vAn -N 2 < /dev/urandom | sed 's/\ *.[0-9]//')) >> results/job_output/$filename;done; done;
